INSERT INTO `company` (`c_id`, `company`, `desc`, `place`, `name`, `apply`, `phone`, `email`, `website`, `instagram`, `facebook`, `youtube`, `c_logo`, `date`, `status`, `feature`) VALUES
(28, 'GBK Gohr + Bobzin Steuerberatungsgesellschaft mbH', '<p>Wir sind eine junge und dynamische Kanzlei in Lübeck in unmittelbarer Nähe der Altstadt sowie in Hamburg-Rothenbaum und Berlin und wir möchten uns wachstumsbedingt personell stetig weiter verstärken. Bei GBK Steuerberater arbeiten aktuell neben 5 Steuerberatern noch 20 weitere Mitarbeiter in verschiedenen Bereichen. Wir sind auf Unternehmen fokussiert und im täglichen Kanzleigeschäft bereits stark digital aufgestellt.</p>', 'Lübeck', 'Herr Stefan Bobzin', 'https://gbk-steuerberater.de/karriere/', '04513844490', 'info@gbk-steuerberater.de', 'https://gbk-steuerberater.de/', '', '', '', 'c_logo_16394299192717.png', '2021-12-13 22:11:59', 1, 0),
(29, 'HansePatrol GmbH', '<p>HansePatrol ist zwar schon seit mehr als 20 Jahren am Markt, entwickelt sich dennoch stetig weiter. Mit modernster Technik, vollausgestatteten Fahrzeugen und einer Übertariflichen Bezahlung kannst du auch Teil unseres Teams sein.</p>', 'Lübeck', 'Pierre A. Knip', '', '0451 59 29 86 86', 'meinjob@hansepatrol.de', 'https://hansepatrol.de', '', '', '', 'c_logo_16414738919158.jpeg', '2022-01-06 13:58:11', 1, 0),
(30, 'Lübeck Live Internet GmbH', '<p>Nachrichten aus Lübeck und der Region.</p>', 'Lübeck', 'Volker Gerstmann', '', '', 'news@HL-live.de', 'https://www.HL-live.de', '', '', '', 'c_logo_16417399739095.png', '2022-01-09 15:52:53', 1, 0),
(31, 'Konditorei Junge GmbH', '<p><strong>Bei uns ist dein Job vor allem eins: sicher.</strong></p><p>Es gibt viele Gründe, die für Junge als deinen neuen Arbeitgeber sprechen. Hier sind die fünf wichtigsten (alle anderen findest du auf&nbsp;<a href=\"http://jb.de/extras\">jb.de/extras</a>):</p><p>·&nbsp;&nbsp;&nbsp; Junge-Arbeitsplätze sind krisensicher (Corona hat das bestätigt)</p><p>·&nbsp;&nbsp;&nbsp; Junge lebt das familiäre Miteinander (schaut gern in die Bewertungen)</p><p>·&nbsp;&nbsp;&nbsp; Junge sorgt für Teamspirit, der für Spaß an der Arbeit steht</p><p>·&nbsp;&nbsp;&nbsp; Junge-Arbeitsplätze sind top-modern -­ egal, ob im Verkauf, in der Produktion oder in der Verwaltung, die bei uns Service Center heißt</p><p>·&nbsp;&nbsp;&nbsp; Junge fördert Vielfalt, die frei von Vorurteilen ist und ALLE Mitarbeiterinnen und Mitarbeiter gleichermaßen wertschätzt</p><p>Und es gibt noch einiges mehr: Seit unserer Gründung 1897 haben wir uns die Freude an der Veränderung bewahrt. Wir suchen ständig neue Trends, probieren aus und entwickeln neue Rezepturen. Das alles machen wir im Team. Darin liegt unsere Stärke, das hält uns seit 125 Jahren knackfrisch. So sind wir zu einem durch und durch gesunden Unternehmen mit mehr als 200 Filialen gewachsen, das 4.300 sichere Arbeitsplätze und Top-Karrieremöglichkeiten bietet.</p>', 'Lübeck', 'Carolin Weber', 'https://www.jb.de/jobs/', '0451 38875 158', 'C.Weber@jb.de', 'https://www.jb.de/', '', '', '', 'c_logo_16418097819492.png', '2022-01-10 11:15:21', 1, 1),
(32, 'Physiotherapie Lohmühle Bastian Stender', '<p>Wir bieten Ihnen ein vielfältiges Angebot rund um die Physiotherapie im Kinder- und Erwachsenenbereich. Unser freundliches Rezeptionsteam empfängt Sie und beantwortet gerne Ihre Fragen und Anliegen.</p>', 'Lübeck', 'Bastian Stender', 'http://www.physiotherapie-lohmuehle.de/stellen.php', '0451 479 11 51', 'info@physiotherapie-lohmuehle.de', 'https://www.physiotherapie-lohmuehle.de/', '', '', '', 'c_logo_16418137595924.png', '2022-01-10 12:22:39', 1, 0),
(33, 'Freitag & Sohn', '<p>Die Firma Freitag &amp; Sohn blickt auf ihr 100-jähriges Bestehen zurück. Langjährige Handwerkstradition verbunden mit hochmoderner Technik steht hinter dem bekannten Firmennamen.</p>', 'Lübeck', 'Dirk von Malottki', 'https://www.freitag-sohn.de/unternehmen/shk/recruiting', '0451 67051', 'info@freitag-sohn.de', 'https://www.freitag-sohn.de/', '', '', '', 'c_logo_16418147304824.png', '2022-01-10 12:38:50', 1, 0),
(34, 'Brandenburg Aufzüge und Elektrotechnik GmbH', '<p>Wir, die Firma Brandenburg GmbH, sind ein familiengeführter mittelständischer Elektromeisterfachbetrieb mit über 20-jähriger Erfahrung in der Lift- und Aufzugstechnik.</p>', 'Lübeck', 'Jan Brandenburg', '', '0451 30 80 991', 'info@brandenburg-aufzuege.de', 'https://www.brandenburg-aufzuege.de/', '', '', '', 'c_logo_16418181223244.jpg', '2022-01-10 13:35:22', 1, 0),
(35, 'beda Telemarketing GmbH', '<p>Seit über 10 Jahren arbeiten wir für Auftraggeber aus den verschiedensten Branchen. Wir würden uns freuen, wenn auch Du bald zu unserem Team gehörst!</p>', 'Lübeck', 'Kati Nehlsen', 'https://beda-telemarketing.de/?page_id=147', '0451 40 90 23 01', 'kati.nehlsen@beda-telemarketing.de', 'https://beda-telemarketing.de/', '', '', '', 'c_logo_16418209561597.png', '2022-01-10 14:22:36', 1, 0),
(36, 'Ingenieurbüro Sachs & de Buhr', '<p style=\"text-align:justify;\">Das Ingenieurbüro Sachs &amp; de Buhr mit Sitz in Lübeck ist ein Planungsbüro, das seit 1991 vornehmlich im Bereich der Umwelttechnik tätig ist. Unser Team aus Ingenieuren, Technikern und Bauzeichnern projektiert Vorhaben im Tief- und Landschaftsbau sowie der Wasser- und Abfallwirtschaft.</p>', 'Lübeck', 'Rüdiger Sachs', '', '0451799 22 0', 'info@sachsdebuhr.de', 'https://www.sachsdebuhr.de/', '', '', '', 'c_logo_16418258022333.png', '2022-01-10 15:43:22', 1, 0),
(37, 'HR7 GmbH – The Job Factory', '<p>Als Familienunternehmen in der Personaldienstleistungsbranche steht bei HR7 der Mitarbeiter im Mittelpunkt. Bei uns werden Sie individuell betreut und gefördert – mit viel persönlicher Note. Gemäß Ihrer Qualifikationen und Vorstellungen finden wir den Job, der zu Ihnen passt. Davon dürfen Sie sich auch gerne selbst überzeugen, denn als mehrfach ausgezeichnete Top-Company sprechen die Bewertungen unserer Mitarbeiter für sich.</p>', 'Lübeck', 'Herr Philipp Kowalski', 'https://hr7-gmbh.de/jobs/', '0451 307 217 16', 'p.kowalski@hr7-gmbh.de', 'https://hr7-gmbh.de/', '', '', '', 'c_logo_16418934883979.jpg', '2022-01-11 10:31:28', 1, 0),
(38, 'hestomed nord GmbH', '<p>Die hestomed nord GmbH in Stockelsdorf betreut Rettungsdienste in Norddeutschland nach dem Motto „Mehr als nur Standard“. Wir sind autorisiertes Service- und Vertriebszentrum für Defibrillatoren der Marke corpuls und Notfallbeatmung aus dem Hause Dräger. Von unserem Standort aus versorgen wir unsere Kunden mit Geräten und Verbrauchsmaterial. Für unsere Serviceabteilung suchen wir einen Servicetechniker, der für die Prüfung und Reparatur der Geräte in unser Werkstatt und auch bei unseren Kunden vor Ort mit unseren mobilen Werkstätten (VW Crafter) verantwortlich ist. Quereinsteiger mit einer handwerklich/technischen Ausbildung sollen sich hier gern angesprochen fühlen. Nach einer intensiven Einschulung auf unsere Produkte besetzen Sie abwechselnd ein Servicemobil und die Werkstatt. Geplant ist eine Woche Außendienst mit Übernachtungen und anschließend eine Woche Innendienst.</p>', 'Lübeck', 'Herr Sven Larsen', 'https://www.hestomed-nord.de/unternehmen/karriere.html', '045170787825', 's.larsen@hestomed-nord.de', 'https://www.hestomed-nord.de/', '', '', '', 'c_logo_16419017774045.png', '2022-01-11 12:49:37', 1, 0),
(39, 'WSP Personaldienst GmbH', '<p><strong>WSP-Personaldienst Ihre Spezialisten für Personalwirtschaft</strong></p><p>Das WSP-Team ist ein kommunikativer und strategischer Partner im Bereich der modernen Personaldienstleistung. Kundenzufriedenheit und die Zufriedenheit unserer Mitarbeiter ist unsere Leitthese. Die Personaldienstleistung ist zu einem unverzichtbaren Bestandteil innovativer Personalpolitik geworden. Das WSP Team in Lübeck steht Ihnen zur pünktlichen Erfüllung, auch kurzfristiger Termine, zur nachhaltigen Sicherung des Unternehmenserfolgs unterstützend zur Seite. Ihr gutes Personal ist eine entscheidende Schnittstelle zwischen Ihnen und Ihrem Kunden. Kein Unternehmen kann sich vor dem immer härter werdenden Wettbewerb und der zunehmenden Globalisierung der Märkte personelle Defizite leisten - zunehmend ist eine stärkere Flexibilität gefragt.</p>', 'Lübeck', 'Frau Lüdemann-Oldenburg', 'https://www.wsp-personaldienst.de/stellenangebote', '0451 / 397070', 'luedemann@wsp-personaldienst.de', 'https://www.wsp-personaldienst.de/', '', '', '', 'c_logo_16419850378315.png', '2022-01-12 11:57:17', 1, 0),
(40, 'Hanseatische Rohrreinigung GmbH', '<p>Wir sind ein junges, stetig wachsendes Unternehmen, welches im März 2017 gegründet wurde. Zurzeit kümmern sich 17 Mitarbeiter*innen in verschiedenen Bereichen um das Wohl des Kunden. Wir sind ein zertifiziertes Fachunternehmen im Bereich der Rohrsanierung, Rohrreinigung und -Reparatur für Privat- und gewerbliche Kunden. Die Schlauchliner-Sanierung sowie die Erstellung von Dichtheitsprüfungen und Bestandsplänen gehören zu unserem täglichen Aufgabenbereich. Verstopfungsursachen mit der TV-Kamera festzustellen und zu Orten „damit‘s wieder läuft“ gehören ebenfalls zum Tagesgeschäft.</p>', 'Lübeck', 'Fr. Roos-Meyer/ Hr. Kwiring', '', '0451-2097920', 'info@hanseatische-rohrreinigung.de', 'https://www.hanseatische-rohrreinigung.de', '', '', '', 'c_logo_16420762543756.png', '2022-01-13 13:17:34', 1, 0),
(41, 'Starpool Finanz GmbH - Ein Netzwerkunternehmen der Hypoport SE', '<p><strong>Starpool Finanz GmbH</strong></p><p>Starpool ist ein Gemeinschaftsunternehmen der DSL Bank – eine Niederlassung der Deutsche Bank AG und dem Technologie-Netzwerk <strong>Hypoport SE</strong>.&nbsp;<br>Ein Pool voller Möglichkeiten - Wir bieten unseren Partnern eine Plattform zur Vermittlung von Finanzierungsprodukten an und unterstützen sie mit erstklassigem Service, innovativer Technologie und den richtigen Kolleg:innen.</p><p><strong>Was wir machen?&nbsp;</strong>&nbsp;Wir bei Starpool unterstützen unsere Vertriebspartnern dabei, Menschen ihre Träume zu erfüllen - mit erstklassigem Service, Technologie und den richtigen Produkten.</p>', 'Lübeck', 'Marian Dohn', 'https://karriere.hypoport.de/jobs/customer-support-agent-1st-level-quereinsteiger-w-m-d-1/', '0451 1408 9223', 'Marian.Dohn@hypoport.de', 'https://www.starpool.de/', '', '', '', 'c_logo_16421068405620.png', '2022-01-13 21:47:20', 1, 0),
(42, 'Kinder-Hafen', '<p>Der Kinder-Hafen gUG(haftungsbeschränkt) ist ein gemeinnütziger freier Träger. Wir betreuen Grundschulkinder vor und nach dem Unterricht in der Willy-Brandt-Schule in Lübeck. Projekt \"Ganztag an Schule\" in Kooperation mit der Hansestadt Lübeck und dem Schulträger der Hansestadt Lübeck.</p>', 'Lübeck', 'Frau Stephanie Mikos / Herrn Jan Scheel', '', '0451 691600', 'Kinder-Hafen@t-online.de', '', '', '', '', 'c_logo_16425044845261.jpg', '2022-01-18 12:14:44', 1, 0),
(43, 'T&T Transporte GmbH', '<p>Wir sind ein zertifizierter Entsorgungsfachbetrieb und Spediteur im Norden Deutschlands mit Sitz in Lübeck. Unsere Dienstleistungen umfassen die Bereiche Entsorgung, Schüttgüter und Transporte. Als zertifizierter Entsorgungsfachbetrieb verfügen wir über die nötigen Kenntnisse, sowie Zertifikate um anfallende Böden und Abfälle fachgerecht und umweltbewusst&nbsp; zu entsorgen.&nbsp;</p><p>Unser Unternehmen wird Inhaber geführt und zeichnet sich mit einem familiären Miteinander aus.</p>', 'Lübeck', 'Herr Weu', '', '04513080938', 'weu@tt-transporte.de', 'https://www.tt-transporte.de/', '', '', '', 'c_logo_16426050075166.png', '2022-01-19 16:10:07', 1, 0),
(44, 'SeniorInnenEinrichtungen der Hansestadt Lübeck', '<p>Mit 7 Einrichtungen, 590 Pflegeplätzen, 330 Plätzen für „betreutes Wohnen“ bieten die SeniorInnenEinrichtungen der Hansestadt Lübeck seit über 50 Jahren ein Zuhause für die Lübecker Bürger:innen. Dabei gewährleistet die städtische Trägerschaft Sicherheit, Kontinuität und Verlässlichkeit. Und die übergeordnete Verwaltungsstruktur sorgt für schnelle und einfache Abläufe.</p>', 'Lübeck', 'Herr Diekert', 'https://www.aph-luebeck.de/karriere/jobboerse', '04516099041', 'diekert@aph-luebeck.de', 'https://www.aph-luebeck.de', '', '', '', 'c_logo_16426830372022.png', '2022-01-20 12:46:17', 1, 1),
(45, 'Stegmann Tortechnik GmbH', '<p>Die Firma Stegmann Tortechnik GmbH ist von Lübeck aus kompetenter Ansprechpartner für individuelle Garagentorlösungen in Schleswig-Holstein und Hamburg. Für unsere Kundinnen und Kunden leisten wir die Beratung, den Verkauf, die Montage und die Durchführung aller anfallenden Gewerke rund ums Thema Garagentore und Antriebe.</p>', 'Lübeck', 'Herr Teßmer', '', '015157935073', 'info@stegmann-tortechnik.de', 'https://www.stegmann-tortechnik.de', '', '', '', 'c_logo_16431965207500.png', '2022-01-26 12:28:40', 1, 0),
(46, 'DRK-Schwesternschaft Lübeck e.V.', '<p>Die DRK-Schwesternschaft bietet seit über 100 Jahren hochqualifizierte Pflegeleistungen an und ist seitdem kompetenter Partner für den Bereich Pflege innerhalb der internationalen Rotkreuz- und Rothalbmond-Bewegung. Wir handeln nach den sieben Rotkreuz-Grundsätzen. Neben verschiedenen Pflegeeinrichtungen und dem Krankenhaus Rotes Kreuz Lübeck betreiben wir eine Kindertagesstätte, die von 6.00 bis 21.00 Uhr geöffnet hat. Unser Team besteht aus 15 pädagogischen Fachkräften, die für eine Krippen-, zwei Elementar- und eine Naturgruppe verantwortlich sind.</p>', 'Lübeck', 'Bitte den Text unter der Bewerbung beachten', 'https://www.drk-schwesternschaft-luebeck.de/jobs', '0451/98902960', 'Tanja.Ahrens@drk-schwesternschaft-luebeck.de', 'https://www.drk-schwesternschaft-luebeck.de/', '', '', '', 'c_logo_16432159546697.png', '2022-01-26 17:52:34', 1, 1),
(47, 'DRK-Therapiezentrum Marli GmbH', '<p>Das Krankenhaus Rotes Kreuz Lübeck - Geriatriezentrum - ist ein erfolgreich etabliertes Krankenhaus für Geriatrie in Schleswig-Holstein unter der Trägerschaft der DRK-Schwesternschaft Lübeck e.V. und der AMEOS Gruppe. Die DRK-Schwesternschaft Lübeck bietet seit über 100 Jahren hochqualifizierte Pflegeleistungen an und ist seitdem kompetenter Partner für den Bereich Pflege innerhalb der internationalen Rotkreuz- und Rothalbmond-Bewegung. Wir handeln nach den sieben Rotkreuz-Grundsätzen und stehen für einen engen und sektorübergreifenden Kontakt zum Patienten sowie eine multiprofessionelle Zusammenarbeit auf Augenhöhe unter den verschiedenen Teams: von der stationären Betreuung über den therapieintensiven Bereich der Tagesklinik bis hin zur ambulanten Versorgung.</p>', 'Lübeck', 'Bitte den Text unter der Bewerbung beachten', 'https://www.geriatrie-luebeck.de/jobs', '0451/98902960', 'Tanja.Ahrens@drk-schwesternschaft-luebeck.de', 'https://www.geriatrie-luebeck.de/', '', '', '', 'c_logo_16432973492750.png', '2022-01-27 16:29:09', 1, 0),
(48, 'Landespolizei Schleswig-Holstein', '<p><strong>Polizeidirektion für Aus- und Fortbildung und für die Bereitschaftspolizei</strong></p><p>Über 2,8 Millionen Menschen leben im Land zwischen den Meeren – dort, wo die Landespolizei Schleswig-Holstein für Schutz und Sicherheit sorgt. Mit dem Polizeiorganisationsgesetz vom 12. November 2004 gab es eine grundlegende Organisationsreform in der Landespolizei Schleswig-Holstein. Ein Landespolizeiamt (LPA) wurde neu gebildet und das seit 1994 bestehende Landeskriminalamt (LKA) fortgeschrieben. Nachgeordnet sind dem LPA nunmehr sieben Polizeidirektionen auf dem Gebiet der Kreise und kreisfreien Städte und eine Polizeidirektion als Fachdirektion in Eutin für Aus- und Fortbildung und für die Bereitschaftspolizei.</p><p>&nbsp;</p>', 'Eutin', 'Werbe- und Einstellungsstelle', 'https://www.schleswig-holstein.de/DE/Landesregierung/POLIZEI/Berufung/berufung_node.html', '04521 81-55555', 'karriere@polizei.landsh.de', 'https://www.schleswig-holstein.de/DE/Landesregierung/POLIZEI/Polizei_node.html', '', '', '', 'c_logo_16433669678041.png', '2022-01-28 11:49:27', 1, 1),
(49, 'Leserkreis Daheim', '<p>Wir sind Deutschlands größter und modernster Lesezirkel, ein erfolgreiches Traditionsunternehmen mit bundesweit 20 Filialen. Unseren Geschäfts- und Privatkunden bieten wir preiswert und umweltfreundlich individuell zusammengestellte Lesepakete aus einer Auswahl von über 200 Zeitschriften an.</p>', 'Lübeck', 'Herr Carsten Spahl', 'https://leserkreis.de/blogs/karriere-und-jobs', '0451 / 577 07 ', 'Luebeck@leserkreis.de', 'https://leserkreis.de/', '', '', '', 'c_logo_16437292646029.png', '2022-02-01 16:27:44', 1, 1),
(50, 'TT-Line GmbH & Co. KG', '<p><i>TT-Line ist die führende Fährreederei in der südlichen Ostsee, die hochmoderne Fährschiffe zwischen Deutschland, Polen, Litauen und Schweden einsetzt. Dabei befördert TT-Line auf seinen 4 Routen jährlich über 900.000 Fahrgäste sowie mehr als 500.000 Frachteinheiten und ist damit Marktführer im Südschwedenverkehr. TT-Line beschäftigt derzeit ca. 700 Mitarbeiter an Bord und in den Landbüros in Travemünde, Trelleborg, Swinemünde/Stettin, Rostock und Klaipeda.</i></p>', 'Lübeck', 'Frau Maxi Thees', 'https://www.ttline.com/de/ttline/karriere/', '04502 801 980', 'Maxi.Thees@ttline.com', 'https://www.ttline.com/de/', '', '', '', 'c_logo_16438888932431.png', '2022-02-03 12:48:13', 1, 1),
(51, 'Beispiel Firma', '<p>Hier ist eine Beispiel Beschreibung.</p>', 'Firmensitz hier', 'Herr Mustermann', 'https://www.karriereseite.de', '0451 123 123', 'jobs@hl-live.de', 'https://www.webseite.de', '', '', '', 'c_logo_16439074404396.jpg', '2022-02-03 17:57:20', 1, 0),
(52, 'TraveNetz GmbH', '<p>Effizient, hoch verfügbar und der Energiewende verpflichtet – die TraveNetz GmbH steht für den Betrieb und den Ausbau einer modernen Infrastruktur für die Energie- und Wasserversorgung des Wirtschaftsraums Lübeck. Als Betreiberin des Netzes mit den dazugehörigen Leitungen, Schaltanlagen und Umspannwerken gewährleisten wir für unser Netzgebiet eine sichere und zuverlässige Energieversorgung mit Strom, Gas, Wasser und Wärme. Als eine 100 % Tochter der Stadtwerke Lübeck GmbH sind wir verantwortlich für Planung, Errichtung, Betrieb und Ausbau der Netze.</p><p>Als Unternehmen mit einem hohen Verantwortungsbewusstsein für die Region bekennt sich die TraveNetz GmbH uneingeschränkt zu den Zielen der Energiewende und gestaltet Lübecks Energiezukunft aktiv mit: Wir setzen auf umweltschonende und hocheffiziente Anlagen, investieren in die Infrastruktur, Versorgungssicherheit und Netzstabilität und treiben den Ausbau der Infrastruktur für Elektromobilität voran.</p><p><strong>Stadtwerke Gruppe / Holding&nbsp;</strong></p><p>Nachhaltig. Digital. Regional – die Stadtwerke Lübeck Gruppe ist der führende kommunale Anbieter für alle Dienstleistungen rund um Energie, Digitalisierung, Mobilität und Infrastruktur. Mit unseren leistungsfähigen Produkten verbinden wir Lebenswelten, vernetzen Menschen und Unternehmen, fördern die Wertschöpfung vor Ort und unterstützen kraftvoll und engagiert den Sport, die Bildung und das kulturelle Leben. Verantwortungsvoll, gemeinwohlorientiert, der Vielfalt der Menschen und ihren Lebensentwürfen verpflichtet, gestalten wir die Klima- und Mobilitätswende für die Bürger:innen in und um Lübeck. Mit rund 1.400 Mitarbeitenden zählen wir zu den 20 größten Arbeitgebern in Schleswig-Holstein&nbsp;</p>', 'Lübeck', 'Katharina Boesett', 'https://gruppe.swhl.de/karriere/', '0451 888-4120', '', 'https://www.travenetz.de/', '', '', '', 'c_logo_16439809669253.png', '2022-02-04 13:44:02', 1, 1),
(53, 'Stadtverkehr Lübeck GmbH', '<p>Die Stadtverkehr Lübeck GmbH ist gemeinsam mit ihrem Tochterunternehmen, der Lübeck-Travemünder-Verkehrsgesellschaft mbH (LVG),&nbsp;der bedeutendste Mobilitätsdienstleister in der Wirtschaftsregion Lübeck. Insgesamt entscheiden sich im Jahr rund 23 Mio. Menschen auf ihrem Weg zur Arbeit, zur Schule oder in der Freizeit für unser attraktives und innovatives Angebot des öffentlichen Nahverkehrs.</p>', 'Lübeck', 'Herr Andreas Didszun', 'https://stellenangebote.swhl.de/stellenangebote.html', '0451 888-2936', '', 'https://www.sv-luebeck.de/de/', '', '', '', 'c_logo_16443419448107.png', '2022-02-08 18:39:04', 1, 1),
(54, 'TraveKom Telekommunikationsgesellschaft mbH', '<p>Als zentraler Umsetzungspartner der Digitalstrategie sowie als Dienstleister digital vernetzter Städte in der Lübeck- und Trave-Region entwickelt und vertreibt die TraveKom datenbasierte Geschäftsmodelle, Digitalisierungsprojekte und Smart-City-Lösungen. Dabei wird auf das LoRaWAN-Funknetz und weitere Technologien aus dem Bereich Internet of Things (IoT) gesetzt. Mit einer zentralen Datenplattform wird das digitale Gehirn der Stadt aufgebaut.</p>', 'Lübeck', 'Frau Stefanie Beuster', 'https://stellenangebote.swhl.de/', '0451 888-4115', '', 'https://www.travekom.de/', '', '', '', 'c_logo_16444940376992.png', '2022-02-10 12:53:57', 1, 0),
(55, 'Stadtwerke Lübeck Holding', '<p>Die Stadtwerke Lübeck Holding ist mit ihren Geschäftsfeldern und Dienstleistungen im Wirtschaftsraum Lübeck eng mit den Menschen und Unternehmen der Region verbunden. Wir leisten damit Jahr für Jahr einen wichtigen Beitrag für Lübeck und die angrenzenden Gemeinden.</p>', 'Lübeck', 'Katja Luedemann', 'https://gruppe.swhl.de/karriere/', '0451 888-4118', '', 'https://gruppe.swhl.de/gruppe/', '', '', '', 'c_logo_16444946706092.png', '2022-02-10 13:04:30', 1, 1),
(56, 'Stadtwerke Lübeck ', '<p>Die Stadtwerke Lübeck gewährleisten mit ihren Produkten eine sichere und effiziente Energieversorgung, den verantwortungsvollen Umgang mit Ressourcen und stehen für soziales Engagement. Wir leisten damit Jahr für Jahr einen wichtigen Beitrag für Lübeck und die angrenzenden Gemeinden.</p>', 'Lübeck', 'Frau Stefanie Beuster', 'https://gruppe.swhl.de/karriere/', '0451 888-4115', '', 'https://www.swhl.de/', '', '', '', 'c_logo_16449262169662.png', '2022-02-15 12:56:56', 1, 1),
(57, 'MSC GERMANY S.A. & Co. KG', '<p>MSC ist eines der führenden Unternehmen in der Containerverschiffung. Gemäß unserer Philosophie „wo der Kunde ist, ist auch MSC“ bauen wir unser Netzwerk stetig aus, sodass wir neben unserem Headquarter in Genf mit 480 Agenturen in 155 Ländern vertreten sind. Unsere Leidenschaft für die Schifffahrt, sowie der Mix aus modernem Mind-Set und traditionellen Werten legen die Basis für unser nachhaltiges Wachstum.&nbsp;</p><p>So umfasst die MSC-Family bereits über 70.000 Experten weltweit und wächst stetig weiter. Gemäß des Unternehmensgeistes unserer Eigentümerfamilie arbeiten wir jeden Tag proaktiv und mit Leidenschaft daran, unseren Kunden innovative Lösungen und Services anbieten zu können. &nbsp;</p>', '', 'Simone Stockfleth', 'https://www.msc.com/deu/careers', '+49 4030295 1487', 'DEU-Careers@msc.com', 'https://www.msc.com/deu', '', '', '', 'c_logo_16457940195085.png', '2022-02-25 14:00:19', 1, 0),
(58, 'js|com GmbH', '<p>Egal für welchen EDV-Bereich Sie Hilfe benötigen, wir haben die Fachleute, die Ihnen zur Seite stehen. Mit Fachkräften, die Ihnen sowohl vor Ort, als auch per Fernwartung zur Verfügung stehen, bringen wir Sie an Ihr Ziel.</p>', 'Lübeck', 'Julian Sack', 'https://www.jscom.de/js_unternehmen.html', '0451 - 476533', 'vertrieb@jscom.de', 'https://www.jscom.de/', '', '', '', 'c_logo_16461334889343.png', '2022-03-01 12:18:08', 1, 0),
(59, 'Pandora Store CITTI-PARK Lübeck', '<p>Selbstständig wertvolle Elemente zu einem Schmuckstück gestalten. Die dänische Firma Pandora ist die erfolgreichste Schmuckfirma in Deutschland.</p><p>Pandora-Schmuckelemente sind zeitlose Symbole des Glücks, der Träume und der Liebe.</p>', 'Lübeck', 'Frau Debert', '', '0451 - 3174774', '309_luebeck@pandora-deutschland.com', 'https://www.citti-park-luebeck.de/pandora', '', '', '', 'c_logo_16462355654529.png', '2022-03-02 16:39:25', 1, 0),
(60, 'Trinity Uhren & Schmuck', '<p>Trinity Uhren &amp; Schmuck bietet eine große Auswahl an Uhren und Schmuck im Citti-Park Lübeck.<br>Im Séparée gibt es eine Trauring Lounge mit individuellen Terminen für Hochzeitspaare und einer Vielfalt an Ringen und Steinen.<br>Der Service Gedanke wird groß geschrieben und wir bieten Batteriewechsel, Uhrenbandmontage und Ohrlochstechen von qualifizierten Mitarbeitern an .</p>', 'Lübeck', 'Kirsten Thimm', '', '', 'kirsten.thimm@email.de', 'https://www.trinity24.de/', '', '', '', 'c_logo_16463236652308.png', '2022-03-03 17:07:45', 1, 0),
(61, 'Fortbildungsakademie der Wirtschaft (FAW)', '<p>Seit über 30 Jahren ist die Fortbildungsakademie der Wirtschaft (FAW) gGmbH anerkannter Partner der deutschen Wirtschaft. In 11 Bundesländern arbeiten wir mit Arbeitgeber*innen, Arbeitsagenturen, Jobcentern, Renten- und Unfallversicherungsträgern zusammen, um gemeinsam unsere beiden wichtigsten Ziele zu erreichen: die Vermittlung in den 1. Arbeitsmarkt und die langfristige Sicherung bestehender Arbeitsplätze.</p>', 'Akademie Lübeck', 'Volker Jödicke', 'https://jobs.faw.de/', '0451 3844487 18', 'volker.joedicke@faw.de', 'https://www.faw.de/', '', '', '', 'c_logo_16463240276887.png', '2022-03-03 17:13:47', 1, 0),
(62, 'MIS Mecklenburger Immobilien Service GmbH', '<p>Wir sind ein familiengeführtes mittelständiges Dienstleistungsunternehmen mit Sitz in Selmsdorf, Lübeck und Grömitz. Wir haben uns auf den Bereich Tourismus spezialisiert und reinigen Ferienimmobilien, Restaurants, Geschäfts im Tourismusbereich. Natürlich reinigen wir auch herkömmliche Objekte.&nbsp; Mit aktuell rund 50 Mitarbeiter*innen gehören wir zum Mittelstand. Aktuell betreuen wir ca. 400 Ferienappartements und Ferienhäuser in Mecklenburg Vorpommern und Schleswig Holstein. Zu unseren Kunden gehören Novasol, Landal, Edeka Jens, Aldi Nord, Martens Backbedarf, Waterfront Shop &amp; Bistro GmbH &amp; Co.KG, Ostsee Event GmbH &amp; Co.KG, Blaue Wiek GmbH, Schule Ferienvermietung GmbH, DRK Seniorenheim Lübeck und viele mehr.</p>', 'Lübeck', 'Andy Viebranz', 'https://www.m-i-s.de/jobs', '017620457692', 'info@m-i-s.de', 'https://www.m-i-s.de/', '', '', '', 'c_logo_16474232105190.jpg', '2022-03-16 10:33:30', 1, 0),
(63, 'Kartoffelkeller Lübeck', '<p>Wir vom Lübecker Kartoffelkeller legen großen Wert auf frische Produkte. Im Mittelpunkt steht bei uns die Kartoffel, die gesunde, hochwertige Knolle in mehreren Sorten und in Bioqualität, mit ihren unzähligen Variationsmöglichkeiten. „Essen und Trinken hält Leib und Seele zusammen – dieser uralte Spruch gilt nach wie vor.&nbsp;</p>', 'Lübeck', 'Frau Berger', '', '04517 62 34', 'info@kartoffel-keller.de', 'https://www.kartoffel-keller.de/', '', '', '', 'c_logo_16476181137629.jpg', '2022-03-18 16:41:53', 1, 0),
(64, 'Digital Loop GmbH', '<p>Wir sind eine Digital Marketing Agentur mit Standorten in Süd- und Norddeutschland sowie Ecuador, Zypern und Russland. Unser Fokus liegt auf SEO, Content, Marketing, UX, Web Analytics sowie Web-Programmierung und e-Commerce.</p>', '', 'Erik Llerena March', 'https://digital-loop.com/ueber-uns/', '089 41 61 47 83 0', 'jobs@digital-loop.com', 'https://digital-loop.com/', '', '', '', 'c_logo_16496812583317.png', '2022-04-11 14:47:38', 1, 0),
(65, 'Witt Personalservice', '<p>Wir sind ein inhabergeführter Personaldienstleister aus Lübeck und arbeiten seit über 14 Jahren erfolgreich mit unseren Kunden zusammen. Bei uns stehen Sie im Mittelpunkt!&nbsp;</p>', 'Lübeck', 'Herr Maximilian Witt und Herr Dominick Albrecht', 'https://www.personalservice-luebeck.de/jobboerse/', '0451 / 7907890', 'mw@witt-personalservice.de', 'https://www.personalservice-luebeck.de/jobboerse/', '', '', '', 'c_logo_16504796441953.jpg', '2022-04-20 20:34:04', 1, 0),
(66, 'inCare by Piening', '<h2>Für Dich: Vor Ort und vom Fach</h2><p>Dass Deine persönlichen Ansprechpartner für Dich erreichbar sind, ist uns ein großes Anliegen. Deshalb haben wir unsere neue Niederlassung gut erreichbar in der Lübecker Altstadt eröffnet. Unsere Standorte sind nicht nur die Arbeitsplätze unserer Disposition, sondern sollen auch Deine berufliche Heimat sein. Für einen kurzen Schnack, fachlichen Austausch, oder einen Kaffee steht die Tür immer offen. Wir kommen aus dem Gesundheitswesen und wissen was Du täglich beruflich leistest.</p><h2>You care. We care. inCare.</h2><p>Wir sind familiär! Wir sind persönlich für Dich da! Wir übernehmen die Verantwortung! Wir denken an Deine Zukunft! InCare by Piening ist eine spezialisierte Marke der Piening GmbH für Medizin- und Pflegeberufe. Wir sind in der Branche zu Hause und kennen somit auch alle für Dich wichtigen Themen.<br><br>Unser Unternehmen wurde mehrfach als einer der besten Arbeitgeber im deutschen Mittelstand ausgezeichnet und zählt zu den größten Personaldienstleistern in Deutschland und ist nichtsdestotrotz ein Familienunternehmen mit WERTEN, die wir leben!<br><br>Wir setzen uns für die Beschäftigung von schwerbehinderten Menschen ein. Daher werden Schwerbehinderte und Gleichgestellte bei gleicher Eignung bevorzugt berücksichtigt.</p><h2>Werde auch Du ein Teil unseres Teams!</h2><p>Melde Dich gern auf dem kurzen Dienstweg unter 0451/930984-0 oder luebeck@in-care.de bei uns.<br>Wir freuen uns auf Deine Kontaktaufnahme!</p>', 'Lübeck', 'Julian Graf-Geduhn', 'https://www.in-care.de/jobsuche', '0451 / 930984 -0', 'luebeck@in-care.de', 'https://www.in-care.de/', '', '', '', 'c_logo_16515773471755.png', '2022-05-03 13:29:07', 1, 0),
(67, 'Personalhaus Nord-Ost', '<p>Die <strong>Personalhaus Nord-Ost GmbH &amp; Co. KG</strong> besteht aus einem motivierten und erfolgreichen Team mit insgesamt mehr als 30 Jahren Erfahrung im Bereich der Personaldienstleistung, Zeitarbeit und Personalvermittlung. Wir bieten eine Vielzahl von hervorragenden Einstiegsmöglichkeiten und Karrierechancen in vielen unterschiedlichen Wirtschaftsbranchen. Ob im Facharbeiter-, Handwerks-, Produktions- oder im kaufmännischen Bereich.</p>', 'Lübeck', 'Chantal Wichmann', 'https://www.personalhaus-nordost.de/jobboerse/', '+4945120271000', 'info@personalhaus-nordost.de', 'https://www.personalhaus-nordost.de/', '', '', '', 'c_logo_16528976159288.png', '2022-05-18 20:13:35', 1, 0),
(68, 'Waterfront Gastro & Shops GmbH & Co. KG (BeachBay)', '<p>Mit der Beach Bay eröffnete das größte touristische Projekt Norddeutsch- lands. Ein Highlight ist die Markthalle. Hier können die Urlaubsgäste entspannt shoppen und sich durch die Kulinarik unterschiedlichster Länder snacken. Werde Teil unseres jungen Teams und gestalte die Markthalle mit! Gemeinsam schaffen wir an dem für uns schönsten Platz entlang der Ostsee unvergessliche Momente.</p>', 'Lübeck-Travemünde', 'Johanna Dahlmann', 'https://beachbay.de/karriere/', '04502 780 1420', 'j.dahlmann@beachbay.de', 'https://beachbay.de/', '', '', '', 'c_logo_16533375915167.jpg', '2022-05-23 22:26:31', 1, 1),
(69, 'Depolt Immobilien', '<p>Wir sind ein lübecker inhabergeführtes Wohnungsunternehmen und seit über 50 Jahren in der Hansestadt zu Hause. Wir bewirtschaften unseren eigenen Wohnungsbestand und sorgen mit unserer Arbeit dafür, dass sich unsere Mieter über viele Jahre bei uns wohl fühlen. Bei uns gibt es kurze Weg, schnelle Entscheidungen und flache Hierarchien. &nbsp;</p>', 'Lübeck', 'Nils Schaefer', '', '0451 504 996-0', 'personal@depolt.de', 'https://depolt.de/', '', '', '', 'c_logo_16534231752727.png', '2022-05-24 22:12:55', 1, 0),
(70, 'Lübeck-Travemünder-Verkehrsgesellschaft mbH (LVG)', '<p>Die Lübeck-Travemünder-Verkehrsgesellschaft mbH (LVG) gehört als Tochterunternehmen&nbsp;mit der&nbsp;Stadtverkehr Lübeck GmbH&nbsp;zum bedeutendsten Mobilitätsdienstleister in der Wirtschaftsregion Lübeck. Insgesamt entscheiden sich im Jahr rund 23 Mio. Menschen auf ihrem Weg zur Arbeit, zur Schule oder in der Freizeit für unser attraktives und innovatives Angebot des öffentlichen Nahverkehrs.</p>', '', '', '', '', '', 'https://sv-luebeck.de', '', '', '', 'c_logo_16597022224991.png', '2022-08-05 14:23:42', 1, 0),
(71, 'Halle 49 GmbH', '<p>Junges Unternehmen seit 01.05.2021 in historischem Backsteingebäude. Schwerpunkt liegt auf Porsche und Mercedes. Junges Team. Angeschlossene Sattlerei.</p>', 'Lübeck / Kulturwerft Gollan', 'Holger Bohnsack', '', '045187929931', 'hb@halle49.de', 'https://www.halle49.de/', '', '', '', 'c_logo_16602561264467.png', '2022-08-12 00:15:26', 1, 0);
